import React, {useState} from 'react';
import '../../styles/pages/authentication/ResetPasswordStyle.css';

function ResetPasswordPage() {
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Add your logic here to reset the password
        // You can use an API call to update the password

        // Display a success or error message based on the result
        setMessage('Password reset successful.');
    };

    return (
        <div className="reset-password-container" style={{backgroundImage: `url(${process.env.PUBLIC_URL}/map.png)`}}>
            <div className="reset-password-form">
                <h2 className="form-title">Reset Password</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="new-password">New Password:</label>
                        <input
                            type="password"
                            id="new-password"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="confirm-password">Confirm Password:</label>
                        <input
                            type="password"
                            id="confirm-password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                            className="input-field"
                        />
                    </div>
                    <button type="submit" className="submit-button">
                        Reset Password
                    </button>
                </form>
                <p className="message">{message}</p>
            </div>
        </div>
    );
}

export default ResetPasswordPage;
