import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
    width: '100%',
    height: '100%',
};

const center = {
    lat: 30.046981762780838,
    lng: 31.237174094852456,
};

function MyGoogleMap() {
    const [markerPosition, setMarkerPosition] = useState(null);
    const [bookmarkData, setBookmarkData] = useState({
        name: '',
        description: '',
    });

    const handleMapClick = (event) => {
        const lat = event.latLng.lat();
        const lng = event.latLng.lng();
        setMarkerPosition({ lat, lng });
    };

    const handleNameChange = (event) => {
        setBookmarkData({
            ...bookmarkData,
            name: event.target.value,
        });
    };

    const handleDescriptionChange = (event) => {
        setBookmarkData({
            ...bookmarkData,
            description: event.target.value,
        });
    };

    const saveBookmark = () => {
        // TODO: Implement logic to save the bookmark
        // You can send a POST request to your backend API here
        console.log('Saving bookmark:', bookmarkData);
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyC3phyjN4feKgzWWAVQOURiVIkvYlSMWLg">
            <GoogleMap
                mapContainerStyle={containerStyle}
                center={center}
                zoom={14}
                onClick={handleMapClick}
            >
                {markerPosition && <Marker position={markerPosition} />}
            </GoogleMap>

            <div>
                <input
                    type="text"
                    placeholder="Bookmark Name"
                    value={bookmarkData.name}
                    onChange={handleNameChange}
                />
                <textarea
                    placeholder="Bookmark Description"
                    value={bookmarkData.description}
                    onChange={handleDescriptionChange}
                />
                <button onClick={saveBookmark}>Save Bookmark</button>
            </div>
        </LoadScript>
    );
}

export default MyGoogleMap;
