import React, {useState} from 'react';
import axios from 'axios';
import '../styles/Signup.css'; // Import the CSS for styling

function Signup() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        username: '',
    });

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({...formData, [name]: value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Make a POST request to your server to register the user
            const response = await axios.post('http://localhost:3000/api/auth/signup', formData);

            // Handle successful registration, redirect user, or show a success message
            console.log('Registration successful:', response.data);
        } catch (error) {
            // Handle registration errors, display error messages, or handle validation errors
            console.error('Registration failed:', error.response.data);
        }
    };

    return (
        <div className="container">
            <div className="form-container">
                <h2 className="form-title">Signup</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <button type="submit" className="submit-button">
                        Register
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Signup;
