import React from 'react';
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';

import Home from './pages/HomePage';
import Login from './pages/authentication/LoginPage';
import SignUp from './pages/authentication/SignUpPage';
import VerificationSuccess from './pages/authentication/VerificationSuccessPage';
import VerificationFailed from './pages/authentication/VerificationFailedPage';
import ForgotPassword from './pages/authentication/ForgotPasswordPage';
import ResetPassword from './pages/authentication/ResetPasswordPage';
import Profile from './pages/ProfilePage';

function App() {
    const isAuthenticated = localStorage.getItem('authToken');

    return (
        <Router>
            <Switch>
                {/* Public Routes */}
                <Route path="/login" exact component={Login}/>
                <Route path="/signup" exact component={SignUp}/>
                <Route path="/v-success" exact component={VerificationSuccess}/>
                <Route path="/v-error" exact component={VerificationFailed}/>
                <Route path="/forgot-password" exact component={ForgotPassword}/>
                <Route path="/reset-password/:token" exact component={ResetPassword}/>

                {/* Private Routes */}
                <Route
                    path="/home"
                    exact
                    render={() =>
                        isAuthenticated ? <Home/> : <Redirect to="/login"/>
                    }
                />
                <Route
                    path="/profile/:userId"
                    exact
                    render={() =>
                        isAuthenticated ? <Profile/> : <Redirect to="/login"/>
                    }
                />

                {/* Redirect to Login for unknown routes */}
                <Redirect to="/login"/>
            </Switch>
        </Router>
    );
}

export default App;
