import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Redirect, Switch } from 'react-router-dom';

// Custom ProtectedRoute component
const ProtectedRoute = ({ component: Component, isAuthenticated, ...rest }) => (
    <Route
        {...rest}
        render={(props) =>
            isAuthenticated ? (
                <Component {...props} />
            ) : (
                <Redirect to="/login" />
            )
        }
    />
);

// Login page component
const LoginPage = () => {
    // In a real application, you would implement login logic here
    return <h1>Login Page</h1>;
};

// Home page component (protected)
const HomePage = () => {
    return <h1>Home Page (Protected)</h1>;
};

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Simulate authentication check on component mount
    useEffect(() => {
        const authToken = localStorage.getItem('authToken');
        setIsAuthenticated(!!authToken);
    }, []);

    return (
        <Router>
            <Switch>
                <Route path="/login" component={LoginPage} />
                {/* Use the ProtectedRoute for routes that require authentication */}
                <ProtectedRoute
                    path="/home"
                    component={HomePage}
                    isAuthenticated={isAuthenticated}
                />
                {/* Add other routes as needed */}
                <Redirect from="/" to="/home" /> {/* Redirect root to /home */}
            </Switch>
        </Router>
    );
}

export default App;
