import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import '../styles/Login.css'; // Import the CSS for styling

function Login() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });

    const [loginStatus, setLoginStatus] = useState(null);
    const history = useHistory(); // Initialize useHistory

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const apiUrl = 'http://localhost:3000/api/auth/login'; // Update with your API endpoint

        try {
            const response = await fetch(apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                // Handle non-2xx HTTP responses here
                throw new Error('Login failed');
            }

            // Handle success response
            setLoginStatus('success');
            console.log('Login successful');

            // Optionally, you can redirect the user to another page here if needed
            history.push('/dashboard');
        } catch (error) {
            // Handle network error or non-successful response
            setLoginStatus('failed');
            console.error('Login failed:', error);
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2 className="form-title">Login</h2>
                <form onSubmit={handleSubmit}>
                    {/* ... (Rest of the form code) */}
                    <button type="submit" className="submit-button">
                        Login
                    </button>
                </form>
                {loginStatus === 'success' && (
                    <p className="success-message">Login successful!</p>
                )}
                {loginStatus === 'failed' && (
                    <p className="error-message">Login failed. Please try again.</p>
                )}
            </div>
        </div>
    );
}

export default Login;
