import React, {useState} from 'react';
import {Link, useHistory} from 'react-router-dom';
import '../styles/components/NavbarStyle.css';
import LogoutConfirmation from './LogoutConfirmation'; // Import the LogoutConfirmation component

function Navbar() {
    const history = useHistory();
    const [showConfirmation, setShowConfirmation] = useState(false); // State to control the confirmation dialog

    // Check if the user is authenticated (you can use your authentication logic here)
    const isAuthenticated = !!localStorage.getItem('authToken');

    // Function to handle logout
    const handleLogout = () => {
        // Show the confirmation dialog when logging out
        setShowConfirmation(true);
    };

    // Function to confirm logout
    const confirmLogout = () => {
        // Clear the authentication token from localStorage
        localStorage.removeItem('authToken');

        // Redirect to the login page
        history.push('/login');

        // Close the confirmation dialog
        setShowConfirmation(false);
    };

    // Function to cancel logout
    const cancelLogout = () => {
        // Close the confirmation dialog
        setShowConfirmation(false);
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">
                    Map Mark
                </Link>
                <ul className="navbar-menu">
                    <li className="navbar-item">
                        <a href="/home" className="navbar-link">
                            Home
                        </a>
                    </li>
                    <li className="navbar-item">
                        <a href="/profile" className="navbar-link">
                            Profile
                        </a>
                    </li>
                    {isAuthenticated ? (
                        <li className="navbar-item">
                            <a href="/logout" className="navbar-link" onClick={handleLogout}>
                                Logout
                            </a>
                        </li>
                    ) : null}
                </ul>
            </div>
            {showConfirmation && (
                <LogoutConfirmation
                    onCancel={cancelLogout}
                    onConfirm={confirmLogout}
                />
            )}
        </nav>
    );
}

export default Navbar;
