import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/components/NavbarStyle.css';
import LogoutConfirmation from './LogoutConfirmation'; // Import the LogoutConfirmation component

function Navbar() {
    const [showConfirmation, setShowConfirmation] = useState(false); // State to control the confirmation dialog

    // Function to handle logout
    const handleLogout = () => {
        // Show the confirmation dialog when logging out
        setShowConfirmation(true);
    };

    // Function to confirm logout
    const confirmLogout = () => {
        // Perform the logout action here
        // Example: Call an API to log the user out

        // Close the confirmation dialog
        setShowConfirmation(false);

        // Redirect the user to the login page or any desired route
        // Example:
        window.location.href = '/login'; // This will reload the page and clear the session
    };

    // Function to cancel logout
    const cancelLogout = () => {
        // Close the confirmation dialog
        setShowConfirmation(false);
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">
                    Map Mark
                </Link>
                <ul className="navbar-menu">
                    <li className="navbar-item">
                        <a href="/home" className="navbar-link">
                            Home
                        </a>
                    </li>
                    <li className="navbar-item">
                        <a href="/profile" className="navbar-link">
                            Profile
                        </a>
                    </li>
                    <li className="navbar-item">
                        <button className="navbar-link" onClick={handleLogout}>
                            Logout
                        </button>
                    </li>
                </ul>
            </div>
            {showConfirmation && (
                <LogoutConfirmation
                    onCancel={cancelLogout}
                    onConfirm={confirmLogout}
                />
            )}
        </nav>
    );
}

export default Navbar;
