import React, {useState} from 'react';
import axios from 'axios';
import '../styles/Signup.css';
import '../styles/App.css';

function Signup() {
    const [formData, setFormData] = useState({
        email: '', password: '', username: '',
    });

    const [registrationStatus, setRegistrationStatus] = useState(null);

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({...formData, [name]: value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/api/auth/signup', formData);

            // Set registration status to success
            setRegistrationStatus('success');
            console.log('Registration successful:', response.data);

            // Optionally, you can redirect the user to another page here if needed
            // history.push('/another-page');
        } catch (error) {
            // Set registration status to failed
            setRegistrationStatus('failed');
            console.error('Registration failed:', error.response.data);
        }
    };


    return (<div className="container">
            <div className="form-container">
                <h2 className="form-title">Signup</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <button type="submit" className="submit-button">
                        Register
                    </button>
                </form>
                {registrationStatus === 'success' && (<p className="success-message">Registration successful!</p>)}
                {registrationStatus === 'failed' && (
                    <p className="error-message">Registration failed. Please try again.</p>)}
            </div>
        </div>);
}

export default Signup;
