import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar'; // Import your Navbar component
import '../styles/pages/ProfileStyle.css'; // Import your styles

function UserProfile({ match }) {
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Fetch user data by ID when the component mounts
        const userId = match.params.userId;

        async function fetchUserData() {
            try {
                const response = await fetch(`http://localhost:3000/profile/${userId}`, {
                    method: 'GET',
                    headers: {
                        // Add any headers if needed
                    },
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setUser(data); // Update user state with fetched data
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        }

        fetchUserData();
    }, [match.params.userId]);

    return (
        <div className="profile-container">
            <Navbar />
            <div className="profile-card">
                {user ? (
                    <div>
                        <h2 className="profile-title">Profile Information</h2>
                        <div className="profile-info">
                            <div className="profile-item">
                                <label className="profile-label">Email:</label>
                                <span className="profile-text">{user.email}</span>
                            </div>
                            <div className="profile-item">
                                <label className="profile-label">Username:</label>
                                <span className="profile-text">{user.username}</span>
                            </div>
                            {/* Add more fields as needed */}
                        </div>
                    </div>
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </div>
    );
}

export default UserProfile;
