import React, { useState, useEffect } from 'react';
import '../styles/pages/ProfileStyle.css';
import Navbar from '../components/Navbar';

function ProfilePage() {
    const [isEditing, setIsEditing] = useState(false);
    const [userData, setUserData] = useState({
        email: '',
        username: '',
        isVerified: '',
    });

    useEffect(() => {
        // Fetch user data when the component mounts
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        try {
            // Replace 'userId' with the actual user ID or a dynamic value
            const userId = '123'; // Replace with your user ID
            const response = await fetch(`http://localhost:3000/api/user-profile/${userId}`, {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${window.authToken}`, // Include the user's authentication token
                },
            });

            if (response.ok) {
                const data = await response.json();
                setUserData(data);
            } else {
                console.error('Error fetching user data');
            }
        } catch (error) {
            console.error('Error fetching user data:', error);
        }
    };

    // Rest of your component code...

    return (
        <div className="profile-container">
            <Navbar />
            {/* Rest of your component */}
        </div>
    );
}

export default ProfilePage;
