import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Login.css'; // Import the CSS for styling

function Login() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });

    const [loginStatus, setLoginStatus] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/api/auth/login', formData);

            // Set login status to success
            setLoginStatus('success');
            console.log('Login successful:', response.data);

            // Optionally, you can redirect the user to another page here if needed
            // history.push('/dashboard');
        } catch (error) {
            // Set login status to failed
            setLoginStatus('failed');
            console.error('Login failed:', error.response.data);
        }
    };

    return (
        <div className="login-container">
            <div className="form-container">
                <h2 className="form-title">Login</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                            className="input-field"
                        />
                    </div>
                    <button type="submit" className="submit-button">
                        Login
                    </button>
                </form>
                {loginStatus === 'failed' && (
                    <p className="error-message">Login failed. Please try again.</p>
                )}
                <p className="register-link">
                    Not registered? <a href="/signup">Register here</a>
                </p>
            </div>
        </div>
    );
}

export default Login;
