import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import { useJsApiLoader } from '@react-google-maps/api';
import usePlacesAutocomplete, { getGeocode, getLatLng } from 'use-places-autocomplete';

const containerStyle = {
    width: '100%',
    height: '100vh',
};

const defaultCenter = {
    lat: 30.046981762780838,
    lng: 31.237174094852456,
};

const libraries = ['places']; // Load the places library

function MyGoogleMap() {
    const [searchValue, setSearchValue] = useState('');
    const [markerPosition, setMarkerPosition] = useState(null);

    const { isLoaded, loadError } = useJsApiLoader({
        googleMapsApiKey: 'AIzaSyC3phyjN4feKgzWWAVQOURiVIkvYlSMWLg', // Replace with your API key
        libraries,
    });

    const { suggestions, setValue } = usePlacesAutocomplete();

    const onSearchChange = (e) => {
        setSearchValue(e.target.value);
        setValue(e.target.value);
    };

    const onSearchSelect = async (value) => {
        setSearchValue(value);

        try {
            const results = await getGeocode({ address: value });
            const { lat, lng } = await getLatLng(results[0]);
            setMarkerPosition({ lat, lng });
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Search for places..."
                value={searchValue}
                onChange={onSearchChange}
            />
            {isLoaded && !loadError && suggestions && (
                <ul>
                    {suggestions.map((suggestion) => (
                        <li key={suggestion.placeId} onClick={() => onSearchSelect(suggestion.description)}>
                            {suggestion.description}
                        </li>
                    ))}
                </ul>
            )}

            <LoadScript googleMapsApiKey="AIzaSyC3phyjN4feKgzWWAVQOURiVIkvYlSMWLg">
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={defaultCenter}
                    zoom={14}
                >
                    {markerPosition && (
                        <Marker
                            position={markerPosition}
                            title="Selected Location"
                        />
                    )}
                </GoogleMap>
            </LoadScript>
        </div>
    );
}

export default MyGoogleMap;
