import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import '../styles/components/GoogleMapStyle.css';

const containerStyle = {
    width: '100%',
    height: '100vh',
};

const center = {
    lat: 30.046981762780838,
    lng: 31.237174094852456,
};

function MyGoogleMap() {
    const token = localStorage.getItem('authToken');
    const userId = localStorage.getItem('userId');
    const [markers, setMarkers] = useState([]);
    const [activeMarker, setActiveMarker] = useState(null);
    const [isAddingBookmark, setIsAddingBookmark] = useState(false);

    useEffect(() => {
        // Fetch user bookmarks when the component mounts
        fetchUserBookmarks();
    }, []);

    const fetchUserBookmarks = () => {
        // Make a GET request to fetch user bookmarks
        fetch(`http://localhost:3000/api/auth/bookmarks/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json(); // Parse the response as JSON
            })
            .then((data) => {
                // Log the retrieved data
                console.log('User bookmarks:', data);
                // Set the markers based on the retrieved data
                setMarkers(data);
            })
            .catch((error) => {
                // Handle errors, e.g., network errors or invalid response
                console.error('Error fetching bookmarks:', error);
            });
    };

    // Rest of your component code...

    return (
        <LoadScript googleMapsApiKey="Your-API-Key">
            {/* Rest of your component code... */}
        </LoadScript>
    );
}

export default MyGoogleMap;
