import React, {useState} from 'react';
import '../../styles/ForgotPassword.css'; // Import the CSS for styling

function ForgotPasswordPage() {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Add your logic here to send a password reset email
        // You can use an API call to send the email

        // Display a success or error message based on the result
        setMessage('Password reset email sent successfully.');
    };

    return (
        <div className="forgot-password-container">
            <div className="forgot-password-form">
                <h2 className="form-title">Forgot Password</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email:</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="input-field"
                        />
                    </div>
                    <button type="submit" className="submit-button">
                        Send Reset Email
                    </button>
                </form>
                <p className="message">{message}</p>
            </div>
        </div>
    );
}

export default ForgotPasswordPage;
