import React from 'react';
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';

import HomePage from './pages/HomePage';
import LoginPage from './pages/authentication/LoginPage';
import SignUpPage from './pages/authentication/SignUpPage';
import ForgotPasswordPage from './pages/authentication/ForgotPasswordPage';
import ResetPasswordPage from './pages/authentication/ResetPasswordPage';
import VerificationSuccessPage from './pages/authentication/VerificationSuccessPage';
import ProfilePage from './pages/ProfilePage';
import VerificationFailedPage from './pages/authentication/VerificationFailedPage';

function App() {
    const isAuthenticated = localStorage.getItem('authToken');

    return (
        <Router>
            <Switch>
                <Route path="/home" exact component={HomePage}/>
                <Route
                    path="/login"
                    exact
                    render={() =>
                        isAuthenticated ? <Redirect to="/profile"/> : <LoginPage/>
                    }
                />
                <Route
                    path="/signup"
                    exact
                    render={() =>
                        isAuthenticated ? <Redirect to="/profile"/> : <SignUpPage/>
                    }
                />
                <Route path="/forgot-password" exact component={ForgotPasswordPage}/>
                <Route path="/reset-password/:token" exact component={ResetPasswordPage}/>
                <Route
                    path="/v-success"
                    exact
                    component={VerificationSuccessPage}
                />
                <Route
                    path="/v-error"
                    exact
                    component={VerificationFailedPage}
                />
                <Route
                    path="/profile/:userId"
                    exact
                    render={() =>
                        isAuthenticated ? <ProfilePage/> : <Redirect to="/login"/>
                    }
                />
            </Switch>
        </Router>
    );
}

export default App;
