import React, { useState } from 'react';
import '../../styles/pages/authentication/ResetPasswordStyle.css';

function ResetPasswordPage() {
    const [newPassword, setNewPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        try {
            // Make an API call to reset the password
            const response = await fetch('http://localhost:3000/api/auth/reset-password', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ newPassword }),
            });

            if (response.ok) {
                // Password reset was successful
                setMessage('Password reset successful.');

                // Clear the newPassword input field
                setNewPassword('');
            } else {
                // Password reset failed
                setMessage('Error resetting password. Please try again.');
            }
        } catch (error) {
            console.error('Error resetting password:', error);
            setMessage('Internal server error. Please try again later.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="reset-password-container" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/map.png)` }}>
            <div className="reset-password-form">
                <h2 className="form-title">Reset Password</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="new-password">New Password:</label>
                        <input
                            type="password"
                            id="new-password"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                            required
                            className="input-field"
                            disabled={isSubmitting} // Disable the input field while submitting
                        />
                    </div>
                    <button type="submit" className="submit-button" disabled={isSubmitting}>
                        {isSubmitting ? 'Resetting Password...' : 'Reset Password'}
                    </button>
                </form>
                <p className="message">{message}</p>
            </div>
        </div>
    );
}

export default ResetPasswordPage;
