import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';

function ProfilePage() {
    const [userData, setUserData] = useState(null);
    const userId = "651d4426048bfe5dc683a6f9"; // Replace with the actual user ID you want to fetch

    useEffect(() => {
        // Fetch user data from the API
        fetch(`http://localhost:3000/api/auth/profile/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to fetch user data');
                }
                return response.json();
            })
            .then((data) => {
                setUserData(data.data); // Update userData state with fetched data
            })
            .catch((error) => {
                console.error('Error fetching user data', error);
            });
    }, [userId]);

    return (
        <div className="profile-container">
            <Navbar />
            <div className="profile-card">
                <div className="profile-header">
                    <h2 className="profile-title">Profile Information</h2>
                </div>
                {userData && (
                    <div className="profile-info">
                        <div className="profile-item">
                            <label className="profile-label">Email:</label>
                            <p className="profile-value">{userData.email}</p>
                        </div>
                        <div className="profile-item">
                            <label className="profile-label">Username:</label>
                            <p className="profile-value">{userData.username}</p>
                        </div>
                        <div className="profile-item">
                            <label className="profile-label">Is Verified:</label>
                            <p className="profile-value">{userData.isVerified ? 'Yes' : 'No'}</p>
                        </div>
                        {/* Add more user data fields as needed */}
                    </div>
                )}
            </div>
        </div>
    );
}

export default ProfilePage;
