// export default ProfilePage;

import React, {useState} from 'react';
import '../styles/pages/ProfileStyle.css';
import Navbar from '../components/Navbar';

function ProfilePage() {
    const [isEditing, setIsEditing] = useState(false); // Start with inputs disabled
    const [userData, setUserData] = useState({
        email: 'b.alramlawi360@gmail.com',
        username: 'ggg',
        isVerified: 'Yes',
    });

    const handleSaveClick = () => {
        setIsEditing(false);
        // Add logic to save updated data here
    };

    const handleEditClick = () => {
        setIsEditing(true); // Enable inputs when Edit is clicked
    };

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value,
        });
    };

    return (
        <div className="profile-container">
            <Navbar/>
            <div className="profile-card">
                <div className="profile-header">
                    <img
                        src={process.env.PUBLIC_URL + '/profile.png'}
                        alt="ProfilePage"
                        className="profile-image"
                    />
                    <h2 className="profile-title">ProfilePage Information</h2>
                </div>
                <div className={`status-rectangle ${userData.isVerified === 'Yes' ? 'verified' : 'not-verified'}`}>
                    {userData.isVerified === 'Yes' ? 'Verified' : 'Not Verified'}
                </div>
                <div className="profile-info">
                    <div className="profile-item">
                        <label className="profile-label">Email:</label>
                        <input
                            type="text"
                            name="email"
                            value={userData.email}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing} // Disable input when not in edit mode
                        />
                    </div>
                    <div className="profile-item">
                        <label className="profile-label">Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={userData.username}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing} // Disable input when not in edit mode
                        />
                    </div>
                    <button
                        className={isEditing ? "profile-save-button" : "profile-edit-button"}
                        onClick={isEditing ? handleSaveClick : handleEditClick}
                    >
                        {isEditing ? "Save" : "Edit"}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ProfilePage;
