import React, {useState} from 'react';
import '../styles/Signup.css'; // Import the CSS for styling
import {Link, useHistory} from 'react-router-dom'; // Import useHistory for redirection

function Signup() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        username: '',
    });

    const [registrationStatus, setRegistrationStatus] = useState(null);

    const history = useHistory(); // Initialize useHistory

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({...formData, [name]: value});
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const apiUrl = 'http://localhost:3000/api/auth/signup'; // Update with your API endpoint

        fetch(apiUrl, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((data) => {
                // Handle success response
                setRegistrationStatus('success');
                console.log('Registration successful:', data);

                // Optionally, you can redirect the user to another page here if needed
                history.push('/home');
            })
            .catch((error) => {
                // Handle error response
                setRegistrationStatus('failed');
                console.error('Registration failed:', error);
            });
    };

    return (
        <div className="container">
            <div className="form-container">
                <h2 className="form-title">Signup</h2>
                {registrationStatus === 'success' ? (
                    <div className="success-message">
                        Registration successful! Redirecting to the Home page...
                    </div>
                ) : registrationStatus === 'failed' ? (
                    <div className="error-message">
                        Registration failed. Please check your information and try again.
                    </div>
                ) : (
                    <form onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                className="input-field"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                                className="input-field"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input
                                type="text"
                                id="username"
                                name="username"
                                value={formData.username}
                                onChange={handleChange}
                                required
                                className="input-field"
                            />
                        </div>
                        <button type="submit" className="submit-button">
                            Register
                        </button>

                        <p className="login-link">
                            Already have an account? <Link to="/login">Login</Link>
                        </p>
                    </form>
                )}
            </div>
        </div>
    );
}

export default Signup;
