import React, {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import '../styles/pages/ProfileStyle.css';
import Navbar from '../components/Navbar';

function ProfilePage() {
    const token = localStorage.getItem('authToken');
    const {userId} = useParams();
    const [isEditing, setIsEditing] = useState(false);
    const [userData, setUserData] = useState({
        email: '',
        username: '',
        isVerified: false,
    });

    useEffect(() => {
        // Fetch user data from the backend using an HTTP GET request
        fetch(`http://localhost:3000/api/auth/profile/${userId}`, {
            method: 'GET',
            headers: {
                'Authorization': `${token}`, // Include the token in the Authorization header
            },
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.status.statusCode === 200) {
                    setUserData(data.data); // Update userData state with fetched data
                } else {
                    // Handle error, e.g., redirect to an error page
                }
            })
            .catch((error) => {
                console.error('Error fetching user data:', error);
                // Handle error
            });
    }, [userId, token]);

    const handleSaveClick = () => {
        setIsEditing(false);

        // Send updated user data to the backend
        fetch(`http://localhost:3000/api/auth/update-profile/${userId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `${token}`,
            },
            body: JSON.stringify(userData), // Send updated user data
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.status.statusCode === 200) {
                    // Data updated successfully
                    alert('Profile updated successfully');
                } else {
                    // Handle error, e.g., show an error message
                    alert('Profile update failed');
                }
            })
            .catch((error) => {
                console.error('Error updating user data:', error);
                // Handle error
            });
    };

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value,
        });
    };

    return (
        <div className="profile-container">
            <Navbar/>
            <div className="profile-card">
                {token}
                <div className="profile-header">
                    <img
                        src={process.env.PUBLIC_URL + '/profile.png'}
                        alt="ProfilePage"
                        className="profile-image"
                    />
                    <h2 className="profile-title">ProfilePage Information</h2>
                </div>
                <div className={`status-rectangle ${userData.isVerified ? 'verified' : 'not-verified'}`}>
                    {userData.isVerified ? 'Verified' : 'Not Verified'}
                </div>
                <div className="profile-info">
                    <div className="profile-item">
                        <label className="profile-label">Email:</label>
                        <input
                            type="text"
                            name="email"
                            value={userData.email}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing} // Disable input when not in edit mode
                        />
                    </div>
                    <div className="profile-item">
                        <label className="profile-label">Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={userData.username}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing} // Disable input when not in edit mode
                        />
                    </div>
                    <button
                        className={isEditing ? "profile-save-button" : "profile-edit-button"}
                        onClick={isEditing ? handleSaveClick : handleEditClick}
                    >
                        {isEditing ? "Save" : "Edit"}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ProfilePage;
