import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios'; // Import axios for making HTTP requests

function ProfilePage() {
    const { userId } = useParams(); // Get the user ID from the URL parameter
    const [userData, setUserData] = useState({});
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Define a function to fetch user data by ID
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:3000/api/auth/profile/${userId}`);
                if (response.status === 200) {
                    setUserData(response.data); // Set the fetched user data in state
                } else {
                    console.error('Error fetching user data');
                }
            } catch (error) {
                console.error('Error:', error);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUserData(); // Call the fetchUserData function when the component mounts
    }, [userId]);

    return (
        <div className="profile-container">
            {/* Render user data here */}
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <div className="profile-info">
                    <h2>User Profile</h2>
                    <p>Email: {userData.email}</p>
                    <p>Username: {userData.username}</p>
                    {/* Add more user data fields as needed */}
                </div>
            )}
        </div>
    );
}

export default ProfilePage;
