// Navbar.js
import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import '../styles/components/NavbarStyle.css';
import LogoutConfirmation from './LogoutConfirmation'; // Import the LogoutConfirmation component

function Navbar() {
    useHistory();
    const [showLogoutConfirmation, setShowLogoutConfirmation] = useState(false);

    // Check if the user is authenticated
    const isAuthenticated = !!localStorage.getItem('authToken');

    // Function to handle logout
    const handleLogout = () => {
        // Display the logout confirmation dialog
        setShowLogoutConfirmation(true);
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">
                    Map Mark
                </Link>
                <ul className="navbar-menu">
                    <li className="navbar-item">
                        <Link to="/home" className="navbar-link">
                            Home
                        </Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/profile" className="navbar-link">
                            Profile
                        </Link>
                    </li>
                    {isAuthenticated ? (
                        <li className="navbar-item">
                            <button className="navbar-link" onClick={handleLogout}>
                                Logout
                            </button>
                        </li>
                    ) : null}
                </ul>
            </div>
            {showLogoutConfirmation && (
                <LogoutConfirmation onCancel={() => setShowLogoutConfirmation(false)} />
            )}
        </nav>
    );
}

export default Navbar;
