import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';

function HomePage() {
    const [bookmarks, setBookmarks] = useState([]);
    const [loading, setLoading] = useState(true); // Add loading state
    const [error, setError] = useState(null); // Add error state
    const token = localStorage.getItem('authToken');
    const userId = localStorage.getItem('userId');

    useEffect(() => {
        const fetchUserBookmarks = async () => {
            try {
                const response = await fetch(`http://localhost:3000/api/auth/bookmarks/${userId}`, {
                    method: 'GET',
                    headers: {
                        Authorization: token,
                    },
                });

                if (response.status === 200) {
                    const data = await response.json();
                    setBookmarks(data);
                    setLoading(false); // Mark loading as complete
                } else {
                    setError('Error fetching user bookmarks');
                    setLoading(false); // Mark loading as complete with error
                }
            } catch (error) {
                setError(error.message);
                setLoading(false); // Mark loading as complete with error
            }
        };

        fetchUserBookmarks();
    }, [token, userId]);

    return (
        <div className="home-container">
            <Navbar className="navbar" />

            <div className="bookmarks-container">
                <h2>Your Bookmarks</h2>
                {loading ? (
                    <p>Loading...</p>
                ) : error ? (
                    <p>Error: {error}</p>
                ) : (
                    <ul>
                        {Array.isArray(bookmarks) ? (
                            bookmarks.map((bookmark) => (
                                <li key={bookmark._id}>
                                    <strong>Name:</strong> {bookmark.name} <br />
                                    <strong>Description:</strong> {bookmark.description}
                                </li>
                            ))
                        ) : (
                            <p>No bookmarks available.</p>
                        )}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default HomePage;
