// export default ProfilePage;
//
import React, { useState, useEffect } from 'react';
import '../styles/pages/ProfileStyle.css';
import Navbar from '../components/Navbar';

function ProfilePage() {
    const [isEditing, setIsEditing] = useState(false);
    const [userData, setUserData] = useState({
        email: '',
        username: '',
        isVerified: '',
    });
    const userId = 123; // Replace with the actual user ID you want to fetch

    const fetchUserData = async () => {
        try {
            const response = await fetch(`http://localhost:3000/api/auth/${userId}`); // Replace with your API endpoint
            if (response.ok) {
                const data = await response.json();
                setUserData(data); // Update userData state with fetched data
            } else {
                console.error('Failed to fetch user data');
            }
        } catch (error) {
            console.error('Error fetching user data', error);
        }
    };

    useEffect(() => {
        fetchUserData(); // Fetch user data when the component mounts
    }, []);

    const handleSaveClick = () => {
        setIsEditing(false);
        // Add logic to save updated data here
    };

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData({
            ...userData,
            [name]: value,
        });
    };

    return (
        <div className="profile-container">
            <Navbar />
            <div className="profile-card">
                {/* ... rest of your component */}
                <div className="profile-info">
                    <div className="profile-item">
                        <label className="profile-label">Email:</label>
                        <input
                            type="text"
                            name="email"
                            value={userData.email}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="profile-item">
                        <label className="profile-label">Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={userData.username}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing}
                        />
                    </div>
                    <button
                        className={isEditing ? 'profile-save-button' : 'profile-edit-button'}
                        onClick={isEditing ? handleSaveClick : handleEditClick}
                    >
                        {isEditing ? 'Save' : 'Edit'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ProfilePage;
