// export default ProfilePage;
//
import React, {useState, useEffect} from 'react';

function ProfilePage() {
    const [isEditing, setIsEditing] = useState(false);
    const [userData, setUserData] = useState({
        email: '',
        username: '',
        isVerified: false,
    });

    useEffect(() => {
        // Fetch user data when the component mounts
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        try {
            // Make a fetch request to get user data
            const response = await fetch('http://localhost:3000/api/auth/profile/:userId', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${window.authToken}`, // Access the global authentication token
                },
            });

            if (response.ok) {
                const data = await response.json();
                setUserData(data);
            } else {
                console.error('Error fetching user data:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching user data:', error);
        }
    };

    const handleSaveClick = async () => {
        try {
            // Make a fetch request to update user data
            const response = await fetch('http://localhost:3000/api/auth/profile', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${window.authToken}`, // Access the global authentication token
                },
                body: JSON.stringify(userData),
            });

            if (response.ok) {
                setIsEditing(false);
                console.log('Profile updated successfully');
            } else {
                console.error('Error updating profile:', response.statusText);
            }
        } catch (error) {
            console.error('Error updating profile:', error);
        }
    };

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value,
        });
    };

    return (
        <div className="profile-container">
            <h2 className="profile-title">Profile Information</h2>
            <div className="profile-info">
                <div className="profile-item">
                    <label className="profile-label">Email:</label>
                    <input
                        type="text"
                        name="email"
                        value={userData.email}
                        onChange={handleInputChange}
                        className="profile-input"
                        disabled={!isEditing}
                    />
                </div>
                <div className="profile-item">
                    <label className="profile-label">Username:</label>
                    <input
                        type="text"
                        name="username"
                        value={userData.username}
                        onChange={handleInputChange}
                        className="profile-input"
                        disabled={!isEditing}
                    />
                </div>
                <button
                    className={isEditing ? "profile-save-button" : "profile-edit-button"}
                    onClick={isEditing ? handleSaveClick : handleEditClick}
                >
                    {isEditing ? "Save" : "Edit"}
                </button>
            </div>
        </div>
    );
}

export default ProfilePage;
