import React, { useState, useEffect } from 'react';
import '../styles/pages/ProfileStyle.css';
import Navbar from '../components/Navbar';

function ProfilePage() {
    const [isEditing, setIsEditing] = useState(false);
    const [userData, setUserData] = useState({
        email: '',
        username: '',
        isVerified: '',
    });
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Fetch user data when the component mounts
        fetchUserData(); // Define this function to fetch user data
    }, []); // Empty dependency array to run the effect only once on mount

    const handleSaveClick = async () => {
        setIsEditing(false);
        setMessage('');

        try {
            const response = await fetch('http://localhost:3000/api/auth/update-profile/:userId', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(userData),
            });

            if (response.status === 200) {
                setMessage('Profile updated successfully.');
            } else {
                setMessage('Error updating profile. Please try again.');
            }
        } catch (error) {
            console.error('Error updating profile:', error);
            setMessage('Internal server error. Please try again later.');
        }
    };

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData({
            ...userData,
            [name]: value,
        });
    };

    const fetchUserData = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/auth/profile/:userId', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (response.status === 200) {
                const data = await response.json();
                setUserData(data);
            } else {
                setMessage('Error fetching user data. Please try again.');
            }
        } catch (error) {
            console.error('Error fetching user data:', error);
            setMessage('Internal server error. Please try again later.');
        }
    };

    return (
        <div className="profile-container">
            <Navbar />
            <div className="profile-card">
                <div className="profile-header">
                    <img
                        src={process.env.PUBLIC_URL + '/profile.png'}
                        alt="ProfilePage"
                        className="profile-image"
                    />
                    <h2 className="profile-title">Profile Information</h2>
                </div>
                <div className={`status-rectangle ${userData.isVerified === 'Yes' ? 'verified' : 'not-verified'}`}>
                    {userData.isVerified === 'Yes' ? 'Verified' : 'Not Verified'}
                </div>
                <div className="profile-info">
                    <div className="profile-item">
                        <label className="profile-label">Email:</label>
                        <input
                            type="text"
                            name="email"
                            value={userData.email}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing}
                        />
                    </div>
                    <div className="profile-item">
                        <label className="profile-label">Username:</label>
                        <input
                            type="text"
                            name="username"
                            value={userData.username}
                            onChange={handleInputChange}
                            className="profile-input"
                            disabled={!isEditing}
                        />
                    </div>
                    {message && <p className="message">{message}</p>}
                    <button
                        className={isEditing ? 'profile-save-button' : 'profile-edit-button'}
                        onClick={isEditing ? handleSaveClick : handleEditClick}
                    >
                        {isEditing ? 'Save' : 'Edit'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ProfilePage;
