import React, { useState, useEffect } from 'react';
import '../styles/pages/HomeStyle.css';
import Navbar from '../components/Navbar';

function HomePage() {
    const token = localStorage.getItem('authToken');
    const userId = localStorage.getItem('userId');
    const [bookmarks, setBookmarks] = useState([]); // Initialize bookmarks as an empty array

    useEffect(() => {
        const fetchUserBookmarks = async () => {
            try {
                const response = await fetch(`http://localhost:3000/api/auth/bookmarks/${userId}`, {
                    method: 'GET',
                    headers: {
                        Authorization: token,
                    },
                });

                if (response.status === 200) {
                    const data = await response.json();
                    setBookmarks(data);
                } else {
                    console.error('Error fetching bookmarks');
                }
            } catch (error) {
                console.error('Error:', error);
            }
        };

        fetchUserBookmarks();
    }, [token, userId]);

    return (
        <div className="home-container">
            <Navbar className="navbar" />

            <div className="bookmarks-container">
                <h2>Your Bookmarks</h2>
                <ul>
                    {bookmarks.map((bookmark) => (
                        <li key={bookmark._id}>
                            <strong>Name:</strong> {bookmark.name}<br />
                            <strong>Description:</strong> {bookmark.description}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default HomePage;
